import React, { useEffect, useState } from "react";
import { Form, Field } from 'react-final-form';
import "./styles.css";
import API from './API';

export const UserList = (props) => {
  const [userList, setUserList] = useState([])
  const [addUser, setAddUser] = useState('')

  useEffect(() => {
    props.getUsers()
      .then((data) => data.filter(people => people.active))
      .then(people => setUserList(people));
  }, [])
  function deleteHandler(id) {
    API.removeUserById(id)
      .then((data) => data.filter(people => people.active))
      .then(people => setUserList(people));
  }
  function submitHandler(userList, setUserList) {

  }
  return <div>
    <table className="table">
      <thead>
        <tr>
          <th>Имя</th>
          <th>Фамилия</th>
          <th>Пол</th>
          <th>Присоеденился</th>
          <th>Удалить</th>
        </tr>
      </thead>
      <tbody>
        {userList?.length ? userList.map((el) =>
          <tr key={el.id}>
            <><td>{el.name}</td>
              <td>{el.surname}</td>
              <td>{el.gender}</td>
              <td>{el.joined}</td>
              <td><button className="btn" onClick={() => deleteHandler(el.id)}>Удалить</button></td></>
          </tr>) : 'Пользователей нет ....'}
      </tbody>
    </table>
    <Form
    onSubmit={onSubmit}
    validate={validate}
    render={({ handleSubmit }) => (
      <form onSubmit={handleSubmit}>
        <h2>Simple Default Input</h2>
        <div>
          <label>First Name</label>
          <Field name="firstName" component="input" placeholder="First Name" />
        </div>

        <h2>An Arbitrary Reusable Input Component</h2>
        <div>
          <label>Interests</label>
          <Field name="interests" component={InterestPicker} />
        </div>

        <h2>Render Function</h2>
        <Field
          name="bio"
          render={({ input, meta }) => (
            <div>
              <label>Bio</label>
              <textarea {...input} />
              {meta.touched && meta.error && <span>{meta.error}</span>}
            </div>
          )}
        />

        <h2>Render Function as Children</h2>
        <Field name="phone">
          {({ input, meta }) => (
            <div>
              <label>Phone</label>
              <input type="text" {...input} placeholder="Phone" />
              {meta.touched && meta.error && <span>{meta.error}</span>}
            </div>
          )}
        </Field>

        <button type="submit">Submit</button>
      </form>
    )}
  />
  </div >;
};

export default UserList;
