import React, { useState } from "react";

import "./styles.css";


export const UserList = (props) => {
  const [userList, setUserList] = useState([])

  props.getUsers()
    .then((state) => state.filter(people => people.active))
  .then(people => setUserList(people));

return <div>
  <table class="table">
    <thead>
      <tr>
        <th>Имя</th>
        <th>Фамилия</th>
        <th>Пол</th>
        <th>Присоеденился</th>
        <th>Удалить</th>
      </tr>
    </thead>
    <tbody>
      <tr>
        {userList?.length ? userList.map((el) =>
          <><td>{el.name}</td>
            <td>{el.surname}</td>
            <td>{el.gender}</td>
            <td>{el.joined}</td>
            <button class="btn">Удалить</button></>) : 'Пользователей нет ....'}
      </tr>
    </tbody>
  </table>
</div>;
};

export default UserList;
