import React, { useEffect, useState } from "react";
import { Form, Field } from 'react-final-form';
import ErrorWithDelay from './ErrorWithDelay'
import "./styles.css";
import API from './API';

export const UserList = (props) => {
  const [userList, setUserList] = useState([])
  const [addUser, setAddUser] = useState('')
  const sleep = ms => new Promise(resolve => setTimeout(resolve, ms))

  const onSubmit = async values => {
    await sleep(300)
    window.alert(JSON.stringify(values, 0, 2))
  }
  useEffect(() => {
    props.getUsers()
      .then((data) => data.filter(people => people.active))
      .then(people => setUserList(people));
  }, [])
  function deleteHandler(id) {
    API.removeUserById(id)
      .then((data) => data.filter(people => people.active))
      .then(people => setUserList(people));
  }
  function submitHandler(userList, setUserList) {

  }
  return <div>
    <table className="table">
      <thead>
        <tr>
          <th>Имя</th>
          <th>Фамилия</th>
          <th>Пол</th>
          <th>Присоеденился</th>
          <th>Удалить</th>
        </tr>
      </thead>
      <tbody>
        {userList?.length ? userList.map((el) =>
          <tr key={el.id}>
            <><td>{el.name}</td>
              <td>{el.surname}</td>
              <td>{el.gender}</td>
              <td>{el.joined}</td>
              <td><button className="btn" onClick={() => deleteHandler(el.id)}>Удалить</button></td></>
          </tr>) : 'Пользователей нет ....'}
      </tbody>
    </table>
    <Form
      onSubmit={onSubmit}
      initialValues={{ firstName: '' }}
      validate={values => {
        const errors = {}
        if (!values.firstName) {
          errors.firstName = 'Required'
        }
        if (!values.lastName) {
          errors.lastName = 'Required'
        }
        if (!values.gender) {
          errors.gender = 'Required'
        } else if (isNaN(values.gender)) {
          errors.gender = 'Must be a number'
        } else if (values.gender === 'male' || values.gender === 'female') {
          errors.gender = 'Must be >18'
        }
        return errors
      }}
      render={({ handleSubmit, form, submitting, pristine, values }) => (
        <form onSubmit={handleSubmit}>
          <Field name="firstName">
            {({ input, meta }) => (
              <div>
                <label>First Name</label>
                <input {...input} type="text" placeholder="First Name" />
                <ErrorWithDelay name="firstName" delay={1000}>
                  {error => <span>{error}</span>}
                </ErrorWithDelay>
              </div>
            )}
          </Field>
          <Field name="lastName">
            {({ input, meta }) => (
              <div>
                <label>Last Name</label>
                <input {...input} type="text" placeholder="Last Name" />
                <ErrorWithDelay name="lastName" delay={1000}>
                  {error => <span>{error}</span>}
                </ErrorWithDelay>
              </div>
            )}
          </Field>
          <Field name="gender">
            {({ input, meta }) => (
              <div>
                <label>gender</label>
                <input {...input} type="text" placeholder="gender" />
                <ErrorWithDelay name="gender" delay={1000}>
                  {error => <span>{error}</span>}
                </ErrorWithDelay>
              </div>
            )}
          </Field>
          <div className="buttons">
            <button type="submit" disabled={submitting}>
              Submit
            </button>
            <button
              type="button"
              onClick={form.reset}
              disabled={submitting || pristine}
            >
              Reset
            </button>
          </div>
          <pre>{JSON.stringify(values, 0, 2)}</pre>
        </form>
      )}
    />
  </div >;
};

export default UserList;
