import React, { useEffect, useState } from "react";
import { Form, Field } from 'react-final-form';
import ErrorWithDelay from './ErrorWithDelay'
import nextId from "react-id-generator";
import "./styles.css";
import API from './API';

export const UserList = (props) => {
  let [htmlId] = nextId()
  const [userList, setUserList] = useState([])
  const [addUser, setAddUser] = useState({
    id: { htmlId },
    name: '',
    surname: '',
    active: true,
    gender: '',
    // joined: new Date()

  })
  const sleep = ms => new Promise(resolve => setTimeout(resolve, ms))
  const inputHandler = (e) => {
    setAddUser((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };
  const onSubmit = async values => {
    await sleep(300)
    API.addUser(addUser)
      .then((data) => data.filter(people => people.active))
      .then(people => setUserList(people));
  }
  useEffect(() => {
    props.getUsers()
      .then((data) => data.filter(people => people.active))
      .then(people => setUserList(people));
  }, [])
  function deleteHandler(id) {
    API.removeUserById(id)
      .then((data) => data.filter(people => people.active))
      .then(people => setUserList(people));
  }
  function submitHandler(addUser, setUserList) {
    API.addUser({})
      .then((data) => data.filter(people => people.active))

  }
  return <div>
    <table className="table">
      <thead>
        <tr>
          <th>Имя</th>
          <th>Фамилия</th>
          <th>Пол</th>
          <th>Присоеденился</th>
          <th>Удалить</th>
        </tr>
      </thead>
      <tbody>
        {userList?.length ? userList.map((el) =>
          <tr key={el.id}>
            <><td>{el.name}</td>
              <td>{el.surname}</td>
              <td>{el.gender}</td>
              <td>{el.joined}</td>
              <td><button className="btn" onClick={() => deleteHandler(el.id)}>Удалить</button></td></>
          </tr>) : 'Пользователей нет ....'}
      </tbody>
    </table>
    <Form
      onSubmit={onSubmit}
      validate={values => {
        const errors = {}
        if (!values.name) {
          errors.firstName = 'Заполни'
        }
        if (!values.surname) {
          errors.lastName = 'Заполни'
        }
        if (!values.gender && ((values.gender !== 'male') || (values.gender !== 'female') || (values.gender !== 'different'))) {
          errors.gender = 'Выбери gender'
        }
        return errors
      }}
      render={({ handleSubmit, form, submitting, pristine, values }) => (
        <form onSubmit={handleSubmit}>
          <Field name="name">
            {({ input, meta }) => (
              <div>
                <label>Имя</label>
                <input {...input} type="text" placeholder="First Name" onChage={inputHandler}/>
                <ErrorWithDelay name="firstName" delay={1000}>
                  {error => <span>{error}</span>}
                </ErrorWithDelay>
              </div>
            )}
          </Field>
          <Field name="surname">
            {({ input, meta }) => (
              <div>
                <label>Фамилия</label>
                <input {...input} type="text" placeholder="Last Name" onChage={inputHandler} />
                <ErrorWithDelay name="lastName" delay={1000}>
                  {error => <span>{error}</span>}
                </ErrorWithDelay>
              </div>
            )}
          </Field>
          <Field name="gender">
            {({ input, meta }) => (
              <div>
                <div class="radio">
                  <input {...input} id="male" type="radio" name="gender" value="male" onChage={inputHandler}/>
                  <label for="male">Мужской</label>
                  <input {...input} id="female" type="radio" name="gender" value="female" onChage={inputHandler}/>
                  <label for="female">Женский</label>
                  <input {...input} id="female" type="radio" name="gender" value="different" onChage={inputHandler}/>
                  <label for="female">Другое</label>
                </div>
                <ErrorWithDelay name="gender" delay={1000}>
                  {error => <span>{error}</span>}
                </ErrorWithDelay>
              </div>
            )}
          </Field>
          <div className="buttons">
            <button type="submit" disabled={submitting}>
              Submit
            </button>
            <button
              type="button"
              onClick={form.reset}
              disabled={submitting || pristine}
            >
              Reset
            </button>
          </div>
          <pre>{JSON.stringify(values, 0, 2)}</pre>
        </form>
      )}
    />
  </div >;
};

export default UserList;
