import React, { useEffect, useState } from "react";
import { Form, Field } from 'react-final-form';
import ErrorWithDelay from './ErrorWithDelay'
import "./styles.css";
import API from './API';

export const UserList = (props) => {
  const newData = new Date();
  let date = `${newData.getDate()}.${newData.getFullYear()}.${newData.getMonth() + 1}`;
  const [userList, setUserList] = useState([])
  useEffect(() => {
    props.getUsers()
      .then((data) => data.filter(people => people.active))
      .then(people => setUserList(people));
  }, [])
  const onSubmit = async values => {
    API.addUser({ ...values, id: (Math.random() * ((1000000 - 6) + 1)) + 6, active: true, joined: date })
      .then((data) => data.filter(people => people.active))
      .then(people => setUserList(people));
  }
  function deleteHandler(id) {
    API.removeUserById(id)
      .then((data) => data.filter(people => people.active))
      .then(people => setUserList(people));
  }

  return <div>
    <div className="divTable">
      <table className="table">
        <thead>
          <tr>
            <th>Имя</th>
            <th>Фамилия</th>
            <th>Пол</th>
            <th>Присоеденился</th>
            <th>Удалить</th>
          </tr>
        </thead>
        <tbody>
          {userList?.length ? userList.map((el) =>
            <tr key={el.id}>
              <><td>{el.name}</td>
                <td>{el.surname}</td>
                <td>{el.gender}</td>
                <td>{el.joined}</td>
                <td><button className="btn" onClick={() => deleteHandler(el.id)}>Удалить</button></td></>
            </tr>) : 'Пользователей нет ....'}
        </tbody>
      </table>
    </div>
    <div className='divForm'>
      <Form
        onSubmit={onSubmit}
        validate={values => {
          const errors = {}
          if (!values.name) {
            errors.firstName = 'Заполни'
          }
          if (!values.surname) {
            errors.lastName = 'Заполни'
          }
          if (!values.gender && ((values.gender !== 'male') || (values.gender !== 'female') || (values.gender !== 'different'))) {
            errors.gender = 'Выбери gender'
          }
          return errors
        }}
        render={({ handleSubmit, form, submitting, pristine, values }) => (
          <form>
            <Field name="name">
              {({ input, meta }) => (
                <div>
                  <label>Имя</label>
                  <input {...input} type="text" placeholder="First Name" />
                  <ErrorWithDelay name="firstName" delay={1000}>
                    {error => <span>{error}</span>}
                  </ErrorWithDelay>
                </div>
              )}
            </Field>
            <Field name="surname">
              {({ input, meta }) => (
                <div>
                  <label>Фамилия</label>
                  <input {...input} type="text" placeholder="Last Name" />
                  <ErrorWithDelay name="lastName" delay={1000}>
                    {error => <span>{error}</span>}
                  </ErrorWithDelay>
                </div>
              )}
            </Field>
            <Field name="gender">
              {({ input, meta }) => (
                <div>
                  <div class="radio">
                    <input {...input} id="male" type="radio" name="gender" value="male" />
                    <label for="male">Мужской</label>
                    <input {...input} id="female" type="radio" name="gender" value="female" />
                    <label for="female">Женский</label>
                    <input {...input} id="female" type="radio" name="gender" value="different" />
                    <label for="female">Другое</label>
                  </div>
                  <ErrorWithDelay name="gender" delay={1000}>
                    {error => <span>{error}</span>}
                  </ErrorWithDelay>
                </div>
              )}
            </Field>
            <div className="buttons">
              <button type="submit" disabled={submitting}>
                Submit
              </button>
              <button
                type="button"
                onClick={form.reset}
                disabled={submitting || pristine}
              >
                Reset
              </button>
            </div>
          </form>

        )}
      />
    </div >
  </div >;
};

export default UserList;
